package Bui;

public class Meal {
	//food和drink是部件
	private String food;
	private String drink;
	
	public void setFood(String food) {
		this.food = food;
	}
	
	public void setDrink(String drink) {
		this.drink = drink;
	}

	public String getFood() {
		// TODO Auto-generated method stub
		return food;
	}

	public String getDrink() {
		// TODO Auto-generated method stub
		return drink;
	}
}
